import React, { useState, useEffect } from 'react';
import './App.css';
import { BrowserRouter } from 'react-router-dom';
import useLocalStorage from './hooks/useLocalStorage';
import NavBar from './routes/NavBar';
import Routes from './routes/Routes';
import KitchenApi from './api';
import UserContext from './auth/UserContext';
import jwt from "jsonwebtoken";

export const TOKEN_STORAGE_ID = "ktichen-token";

function App() {

  const [favoriteIds, setFavoriteIds] = useState(new Set([]))
  const [currentUser, setCurrentUser] = useState(null);
  const [token, setToken] = useLocalStorage(TOKEN_STORAGE_ID);


  console.debug(
    "App",
    "currentUser=", currentUser,
    "token=", token,
  );

  useEffect(function getUserInfo() {
    console.log("App getUserIinfo", "token=", token)

    async function getCurrentUser() {
      if (token) {
        try {
          let { username } = jwt.decode(token)

          KitchenApi.token = token; // set token here
          let currentUser = await KitchenApi.getCurrentUser(username);
          setCurrentUser(currentUser);
          setFavoriteIds(new Set(currentUser.favorites));
        } catch (e) {
          console.error("App getUserInfo error loading", e);
          setCurrentUser(null);
        }
      }
    }
    getCurrentUser()
  }, [token]);

  async function register(registerData) {
    try {
      let token = await KitchenApi.register(registerData)
      setToken(token)
      return { success: true }
    } catch (e) {
      console.error("signup failed", e)
      return { success: false, e }
    }
  };

  async function login(loginData) {
    try {
      let token = await KitchenApi.login(loginData)
      setToken(token)
      return { success: true }
    } catch (e) {
      return { success: false, e }
    }
  };

  async function logout() {
    setCurrentUser(null)
    setToken(null)
  };

  // check if favorite 'id' is in 'favoriteIds'
  function hasFavoritedRecipe(id) {
    console.debug("User favoriteIds", favoriteIds)
    return favoriteIds.has(id)
  }
  // API call to 'FAVORITE' a recipe
  function favoriteThisRecipe(id) {
    if (hasFavoritedRecipe(id)) return;
    console.log("FavoriteRecipe", currentUser.username)
    KitchenApi.favoriteRecipe(currentUser.username, id);
    setFavoriteIds(new Set([...favoriteIds, id]));
  }
  // API call to 'UNfavorite' a recipe
  function unFavoriteRecipe(recipeId) {
    if (!hasFavoritedRecipe(recipeId)) return
    console.log("unFavoriteRecipe", currentUser.username)
    KitchenApi.removeFavoriteRecipe(currentUser.username, recipeId);
    // const arr= new Array of favorite ids
    // if( id !== id)  arr.push(id)

    const arr = new Set([]);
    favoriteIds.forEach(element => {
      if (element !== recipeId) {
        arr.add(element)
      }
    })
    setFavoriteIds(arr);
  }

  // API call to 'FAVORITE' a variation
  function favoriteThisVariation(recipe) {
    if (hasFavoritedRecipe(recipe.id)) return;
    console.log("FavoriteVariation", currentUser.username)
    const username = currentUser.username;
    const data = { ...recipe, username }
    KitchenApi.favoriteVarRecipe(data);
    setFavoriteIds(new Set([...favoriteIds, recipe.id]));
  }


  return (
    <div className="App">
      <BrowserRouter>
        <UserContext.Provider value={{ currentUser, setCurrentUser, hasFavoritedRecipe, favoriteThisRecipe, favoriteThisVariation, unFavoriteRecipe, favoriteIds }}>
          <div className="App">
            <NavBar logout={logout} />
            <Routes login={login} register={register} />
          </div>
        </UserContext.Provider>
      </BrowserRouter>
    </div>
  );
}

export default App;
